n style=\"color: #c678dd;\">use</span> App\\Mail\\<span style=\"color: #e5c07b;\">WelcomeNewUser</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Bus\\<span style=\"color: #e5c07b;\">Queueable</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Contracts\\Queue\\<span style=\"color: #e5c07b;\">ShouldBeUnique</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Contracts\\Queue\\<span style=\"color: #e5c07b;\">ShouldQueue</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Foundation\\Bus\\<span style=\"color: #e5c07b;\">Dispatchable</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Queue\\<span style=\"color: #e5c07b;\">InteractsWithQueue</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Queue\\<span style=\"color: #e5c07b;\">SerializesModels</span>;</div><div><span style=\"color: #c678dd;\">use</span> Illuminate\\Support\\Facades\\<span style=\"color: #e5c07b;\">Mail</span>;</div><br><div><span style=\"color: #c678dd;\">class</span> <span style=\"color: #e5c07b;\">WelcomeEmailJob</span> <span style=\"color: #c678dd;\">implements</span> <span style=\"color: #e5c07b;\">ShouldQueue</span></div><div>{</div><div>&nbsp; &nbsp; <span style=\"color: #c678dd;\">use</span> <span style=\"color: #e5c07b;\">Dispatchable</span>, <span style=\"color: #e5c07b;\">InteractsWithQueue</span>, <span style=\"color: #e5c07b;\">Queueable</span>, <span style=\"color: #e5c07b;\">SerializesModels</span>;</div><br><div>&nbsp; &nbsp; <span style=\"color: #7f848e;\">/**</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;* Create a new job instance.</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;*</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;* </span><span style=\"color: #c678dd;\">@return</span><span style=\"color: #7f848e;\"> </span><span style=\"color: #e5c07b;\">void</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;*/</span></div><div>&nbsp; &nbsp; <span style=\"color: #c678dd;\">public</span> <span style=\"color: #e06c75;\">$user</span>;</div><div>&nbsp; &nbsp; <span style=\"color: #c678dd;\">public</span> <span style=\"color: #c678dd;\">function</span> <span style=\"color: #56b6c2;\">__construct</span>(<span style=\"color: #e06c75;\">$user</span>)</div><div>&nbsp; &nbsp; {</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"color: #e5c07b;\">$this</span>-&gt;<span style=\"color: #e06c75;\">user</span> <span style=\"color: #56b6c2;\">=</span> <span style=\"color: #e06c75;\">$user</span>;</div><div>&nbsp; &nbsp; }</div><br><div>&nbsp; &nbsp; <span style=\"color: #7f848e;\">/**</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;* Execute the job.</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;*</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;* </span><span style=\"color: #c678dd;\">@return</span><span style=\"color: #7f848e;\"> </span><span style=\"color: #e5c07b;\">void</span></div><div><span style=\"color: #7f848e;\">&nbsp; &nbsp; &nbsp;*/</span></div><div>&nbsp; &nbsp; <span style=\"color: #c678dd;\">public</span> <span style=\"color: #c678dd;\">function</span> <span style=\"color: #61afef;\">handle</span>()</div><div>&nbsp; &nbsp; {</div><div>&nbsp; &nbsp; &nbsp; &nbsp; <span style=\"color: #e5c07b;\">Mail</span>::<span style=\"color: #61afef;\">send</span>(<span style=\"color: #c678dd;\">new</span> <span style=\"color: #e5c07b;\">WelcomeNewUser</span>(<span style=\"color: #e5c07b;\">$this</span>-&gt;<span style=\"color: #e06c75;\">user</span>));</div><div>&nbsp; &nbsp; }</div><div>}</div><br></div></pre><p><br></p><p><b><font color=\"#000000\">Hapi 4: Thirrja e jobs në Controller</font></b></p><p style=\"line-height: 1.8;\">Në rastin tonë&nbsp;<i style=\"font-weight: bold;\">WelcomeEmailController </i>ose rastin tuaj mund të jetë <b><i>CreateUserController</i></b></p><pre clas